content.query:
let maxSummarizedTime = toscalar (
    union isfuzzy=true 
        (
            NetworkCustomAnalytics_protocol_CL
                | where EventTime_t >= {TimeRange:start}
                | summarize max_TimeGenerated=max(EventTime_t)
                | extend max_TimeGenerated = datetime_add('minute',10,max_TimeGenerated)
        ),
        (
            print({TimeRange:start})
            | extend max_TimeGenerated = print_0
            | project max_TimeGenerated
        )
        | summarize maxTimeGenerated = max(max_TimeGenerated) 
    );
union isfuzzy=true 
    (
        _Im_NetworkSession(starttime=todatetime(maxSummarizedTime), endtime=now())
        | where isnotempty(DstAppName)
        | summarize Instances=toint(count()) by DstAppName, bin(TimeGenerated, 6h)
    ),
    (
        NetworkCustomAnalytics_protocol_CL
        | where EventTime_t >= {TimeRange:start}
        | where isnotempty(DstAppName_s)
        | summarize Instances=toint(sum(count__d)) by DstAppName=DstAppName_s, TimeGenerated=bin(EventTime_t, 6h)
    )
    | summarize Instances = sum(Instances) by DstAppName, bin(TimeGenerated, 6h)

